    var mBreakpoint = 991;
    var $window = $(window), winwidth = window.innerWidth;
    var deskPrefix = 'desk_', mobiPrefix = 'mobi_';
    var deskEvent = 'cdz_desktop', mobiEvent = 'cdz_mobile';
